import * as t from "io-ts"

export const RelatedDocument = t.exact(
	t.intersection([
		t.type({
			typ: t.string,
			id: t.string,
			language: t.string,
			groupLangId: t.string,
		}),
		t.partial({
			uid: t.union([t.string, t.null, t.undefined]),
		}),
	]),
)

export type RelatedDocument = t.TypeOf<typeof RelatedDocument>
