import { Color } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	color: t.string,
})

export const ColorDef = new t.Type<WithKey<Color>>(
	"ColorDef",
	(u: unknown): u is WithKey<Color> => isFieldDef(u) && Color.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ color }): WithKey<Color> => ({
					key: color,
					def: {
						type: "Color",
					},
				}),
			),
		)
	},
	(n) => n,
)
