import { Date } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	date: t.string,
})

export const DateDef = new t.Type<WithKey<Date>>(
	"DateDef",
	(u: unknown): u is WithKey<Date> => isFieldDef(u) && Date.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ date }): WithKey<Date> => ({
					key: date,
					def: {
						type: "Date",
					},
				}),
			),
		)
	},
	(n) => n,
)
