import { Embed } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	embed: t.string,
})

export const EmbedDef = new t.Type<WithKey<Embed>>(
	"EmbedDef",
	(u: unknown): u is WithKey<Embed> => isFieldDef(u) && Embed.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ embed }): WithKey<Embed> => ({
					key: embed,
					def: {
						type: "Embed",
					},
				}),
			),
		)
	},
	(n) => n,
)
