import { GeoPoint } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	geopoint: t.string,
})

export const GeoPointDef = new t.Type<WithKey<GeoPoint>>(
	"GeoPointDef",
	(u: unknown): u is WithKey<GeoPoint> => isFieldDef(u) && GeoPoint.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ geopoint }): WithKey<GeoPoint> => ({
					key: geopoint,
					def: {
						type: "GeoPoint",
					},
				}),
			),
		)
	},
	(n) => n,
)
