import { IntegrationField } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	integrationFields: t.string,
})

export const IntegrationFieldDef = new t.Type<WithKey<IntegrationField>>(
	"IntegrationFieldDef",
	(u: unknown): u is WithKey<IntegrationField> => isFieldDef(u) && IntegrationField.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ integrationFields }): WithKey<IntegrationField> => ({
					key: integrationFields,
					def: {
						type: "IntegrationFields",
					},
				}),
			),
		)
	},
	(n) => n,
)
