import { Number as NumberField } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	number: t.string,
})

export const NumberDef = new t.Type<WithKey<NumberField>>(
	"NumberDef",
	(u: unknown): u is WithKey<NumberField> => isFieldDef(u) && NumberField.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ number }): WithKey<NumberField> => ({
					key: number,
					def: {
						type: "Number",
					},
				}),
			),
		)
	},
	(n) => n,
)
