import { Range } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	range: t.string,
})

export const RangeDef = new t.Type<WithKey<Range>>(
	"RangeDef",
	(u: unknown): u is WithKey<Range> => isFieldDef(u) && Range.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ range }): WithKey<Range> => ({
					key: range,
					def: {
						type: "Range",
					},
				}),
			),
		)
	},
	(n) => n,
)
