import { RichText } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	structuredtext: t.string,
})

export const RichTextDef = new t.Type<WithKey<RichText>>(
	"RichTextDef",
	(u: unknown): u is WithKey<RichText> => isFieldDef(u) && RichText.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ structuredtext }): WithKey<RichText> => ({
					key: structuredtext,
					def: {
						type: "StructuredText",
					},
				}),
			),
		)
	},
	(n) => n,
)
