import { Select } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	select: t.string,
	config: t.strict({
		options: t.readonlyArray(t.string),
	}),
})

export const SelectDef = new t.Type<WithKey<Select>>(
	"SelectDef",
	(u: unknown): u is WithKey<Select> => isFieldDef(u) && Select.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ select, config }): WithKey<Select> => ({
					key: select,
					def: {
						type: "Select",
						config,
					},
				}),
			),
		)
	},
	(n) => n,
)
