import { Separator } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	separator: t.string,
})

export const SeparatorDef = new t.Type<WithKey<Separator>>(
	"SeparatorDef",
	(u: unknown): u is WithKey<Separator> => isFieldDef(u) && Separator.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ separator }): WithKey<Separator> => ({
					key: separator,
					def: {
						type: "Separator",
					},
				}),
			),
		)
	},
	(n) => n,
)
