import { Timestamp } from "@prismicio/types-internal/lib/customtypes"
import { either } from "fp-ts"
import { pipe } from "fp-ts/lib/function"
import * as t from "io-ts"

import { isFieldDef, WithKey } from "../../utils"

const codec = t.strict({
	timestamp: t.string,
})

export const TimestampDef = new t.Type<WithKey<Timestamp>>(
	"TimestampDef",
	(u: unknown): u is WithKey<Timestamp> => isFieldDef(u) && Timestamp.is(u.def),
	(u: unknown) => {
		return pipe(
			codec.decode(u),
			either.map(
				({ timestamp }): WithKey<Timestamp> => ({
					key: timestamp,
					def: {
						type: "Timestamp",
					},
				}),
			),
		)
	},
	(n) => n,
)
