import type { UIDContent } from "@prismicio/types-internal/lib/content"
import type { UID } from "@prismicio/types-internal/lib/customtypes"

import type { Renderer } from "../models"

const UIDRenderer: Renderer<UID, UIDContent> = {
	renderV1(uid: UIDContent): unknown {
		return { value: uid.value }
	},

	renderV2(_def: UID, uid: UIDContent): unknown {
		return uid.value
	},

	renderDefault(_: UID): unknown {
		return null
	},

	renderMocks(_def: UID, uid: UIDContent): unknown {
		return uid.value
	},
}

export default UIDRenderer
