import type { EmbedContent } from "@prismicio/types-internal/lib/content"
import type { Embed } from "@prismicio/types-internal/lib/customtypes"

import type { Renderer } from "../../models"

const EmbedRenderer: Renderer<Embed, EmbedContent> = {
	renderV1(content: EmbedContent): unknown {
		return {
			type: "Embed",
			value: {
				oembed: content.all,
			},
		}
	},

	renderV2(_def: Embed, content: EmbedContent): unknown {
		return content.all
	},

	renderDefault(_def: Embed): unknown {
		return {}
	},

	renderMocks(def: Embed, content: EmbedContent): unknown {
		return this.renderV2(def, content)
	},
}

export default EmbedRenderer
