import type { GeoPointContent } from "@prismicio/types-internal/lib/content"
import type { GeoPoint } from "@prismicio/types-internal/lib/customtypes"

import type { Renderer } from "../../models"

const GeoPointRenderer: Renderer<GeoPoint, GeoPointContent> = {
	renderV1(content: GeoPointContent): unknown {
		return {
			type: "GeoPoint",
			value: {
				latitude: content.position.lat,
				longitude: content.position.lng,
			},
		}
	},

	renderV2(_def: GeoPoint, content: GeoPointContent): unknown {
		return {
			latitude: content.position.lat,
			longitude: content.position.lng,
		}
	},

	renderDefault(_def: GeoPoint): unknown {
		return {}
	},

	renderMocks(def: GeoPoint, content: GeoPointContent): unknown {
		return this.renderV2(def, content)
	},
}

export default GeoPointRenderer
