import type { SeparatorContent } from "@prismicio/types-internal/lib/content"
import type { Separator } from "@prismicio/types-internal/lib/customtypes"

import type { Renderer } from "../../models"

const SeparatorRenderer: Renderer<Separator, SeparatorContent> = {
	renderV1(_content: SeparatorContent): unknown {
		return {
			type: "Separator",
		}
	},

	renderV2(_def: Separator, _content: SeparatorContent): unknown {
		return true
	},

	renderDefault(_def: Separator): unknown {
		return true
	},

	renderMocks(def: Separator, content: SeparatorContent): unknown {
		return this.renderV2(def, content)
	},
}

export default SeparatorRenderer
