import type {
	FieldType,
	SlicesTypes,
} from "@prismicio/types-internal/lib/customtypes"

export class IncompatibleMockConfigError extends Error {
	mockConfigType: FieldType | SlicesTypes
	fieldType: FieldType | SlicesTypes

	constructor(
		mockConfigType: FieldType | SlicesTypes,
		fieldType: FieldType | SlicesTypes,
	) {
		super(
			`The ${mockConfigType} mock config is not compatible with the ${fieldType} field.`,
		)

		this.mockConfigType = mockConfigType
		this.fieldType = fieldType
	}
}
