import {
	DocumentLinkContent,
	DocumentLinkType,
} from "@prismicio/types-internal/lib/content"
import type { Link } from "@prismicio/types-internal/lib/customtypes"

function random(): DocumentLinkContent {
	return { __TYPE__: DocumentLinkType, id: "mock_document_id" }
}

export const DocumentLinkMock = {
	generate(_def: Link): DocumentLinkContent {
		return DocumentLinkContent.encode(random())
	},
}
