/*
 ** This type is not exhaustif because typescript can't compute all the possible solutions if we try to be explicit
 ** more information: https://medium.com/@steve.alves2/how-to-type-hex-colors-in-typescript-3c3b9a32baa7
 */
export type HexaColorCode = `#${string}`

const hexaColorCodeRegex = /^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$/
export const HexaColor = {
	is: (strColor: string): strColor is HexaColorCode =>
		hexaColorCodeRegex.test(strColor),
}
